package com.abdurashidov.dictionary.adaptersimport android.annotation.SuppressLintimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.abdurashidov.dictionary.databinding.RvItemBindingimport com.abdurashidov.dictionary.models.ApiResponseclass RvAdapter(val list:List<ApiResponse>): RecyclerView.Adapter<RvAdapter.Vh>() {    inner class Vh(val rvItem: RvItemBinding): RecyclerView.ViewHolder(rvItem.root){        @SuppressLint("SetTextI18n")        fun onBind(label: ApiResponse, position: Int){            rvItem.word.text="${label.word}  (${label.meanings[position].partOfSpeech})"            var definition="Definition: "            for (meaning in label.meanings) {                meaning.definitions.forEach {                    definition+="\n${it.definition}"                }            }            rvItem.definition.text= definition            var audio="Audios: "            for (phonetic in label.phonetics) {                audio+="\n${phonetic.audio}"            }            rvItem.phonetics.text=audio            var synonym="Synonym: "            for (meaning in label.meanings) {                for (s in meaning.synonyms) {                    synonym+="\n$s"                }            }            rvItem.phonetics.text=synonym        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Vh {        return Vh(RvItemBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: Vh, position: Int) {        holder.onBind(list[position], position)    }    override fun getItemCount(): Int {        return list.size    }}