package com.abdurashidov.dictionary.viewmodelimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.abdurashidov.dictionary.repository.DictionaryRepository@Suppress("UNCHECKED_CAST")class MyViewModelFactory(private val dictionaryRepository: DictionaryRepository):ViewModelProvider.Factory {    override fun <T : ViewModel> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(MyDictViewModel::class.java)){            return MyDictViewModel(dictionaryRepository) as T        }        throw java.lang.IllegalArgumentException("Error")    }}